'''
This function turns the z scores which are currently in sring form into float form and then finds the min and max values for variance.
'''
def int_calculator(list_l):
    int_calc=[] #temporary list where float values are sent 
    minimum=[]  #list of minimum values of all proteins [data type, protein code, value]
    maximum=[]  ##list of maximum values of all proteins [data type, protein code, value]
    i=0
    for i in range(len(list_l[i])+1):
        if i<len(list_l):
            j=0
            for j in range(len(list_l[i])+1):  #Skips over identifying info and mean z scores
                #print(range(len(list_l[i])))
                if j<=83:
                    j+=1
                elif 83<j<len(list_l[i]):   #Variance 83 is the last mean z score in list and is not included in range
                    if list_l[i][j]=='-': #avoid error of data type
                        j+=1
                    elif list_l[i][j]=='NA':   #avoid error of data type
                        j+=1
                    else:
                        int_calc.append([float(list_l[i][j]), features[j]])                    
                    #int_calc.append(float(list_l[i][j]))
                elif j==len(list_l[i]):  #end of one protein values, uses info in int_calc and finds min max and proceeds to clear int_calc
                    minimum.append(['min', list_l[i][0], min(int_calc)]) 
                    maximum.append(['max', list_l[i][0], max(int_calc)])
                    #print( 'min', min(int_calc))
                    int_calc.clear()
                    
        elif i==len(list_l):  #End of input data sent min and max to out put file and return min max
            with open(output_file, 'a') as file:
                writer=csv.writer(file) 
                header=['data type', 'protein code', 'molecular feature z score']
                writer.writerow(header)
                writer.writerows(minimum)
                writer.writerows(maximum)
            return (minimum, maximum)

